// Icon paths ah cloud-compatible format la fix pannanum
export function fixIconPaths(node: any): any {
  if (!node.GuiSettings) return node;

  // Remove or fix icon paths that cause network blocks
  if (node.GuiSettings.Icon) {
    delete node.GuiSettings.Icon;
  }

  // Plugin-based icon mapping for cloud
  const plugin = node.GuiSettings["@Plugin"] || "";
  
  // Cloud la default icons use pannanum
  const iconMap: { [key: string]: string } = {
    "AlteryxBasePluginsGui.UniversalInput.UniversalInput": "data:image/svg+xml;base64,",
    "AlteryxBasePluginsGui.UniversalOutput.UniversalOutput": "data:image/svg+xml;base64,",
    "AlteryxBasePluginsGui.Filter.Filter": "data:image/svg+xml;base64,",
    "AlteryxBasePluginsGui.Formula.Formula": "data:image/svg+xml;base64,",
    "AlteryxBasePluginsGui.Join.Join": "data:image/svg+xml;base64,",
    "AlteryxBasePluginsGui.Union.Union": "data:image/svg+xml;base64,",
    "AlteryxBasePluginsGui.Sort.Sort": "data:image/svg+xml;base64,",
    "AlteryxBasePluginsGui.Select.Select": "data:image/svg+xml;base64,"
  };

  // Embedded icon set pannanum
  if (iconMap[plugin]) {
    node.GuiSettings.Icon = iconMap[plugin];
  }

  return node;
}

// All external resource paths ah remove pannanum
export function removeExternalResources(jsonString: string): string {
  let cleaned = jsonString;

  // Remove file:// paths
  cleaned = cleaned.replace(/file:\/\/[^\s"'}]+/g, '');
  
  // Remove C:\ paths
  cleaned = cleaned.replace(/C:\\\\[^\s"'}]+/g, '');
  
  // Remove http/https external URLs (except data: URLs)
  cleaned = cleaned.replace(/https?:\/\/(?!data:)[^\s"'}]+/g, '');

  return cleaned;
}

// GuiSettings la unnecessary fields remove pannanum
export function cleanGuiSettings(node: any): any {
  if (!node.GuiSettings) return node;

  // Remove fields that cause network issues
  const fieldsToRemove = [
    'Icon',
    'IconPath', 
    'IconUrl',
    'HelpUrl',
    'ExternalUrl'
  ];

  fieldsToRemove.forEach(field => {
    if (node.GuiSettings[field]) {
      delete node.GuiSettings[field];
    }
  });

  return node;
}
